import React from 'react';
import './App.css';
import { Card, Icon} from 'antd';
import Nav from './Nav';
import {connect} from 'react-redux';

const { Meta } = Card;
function ScreenMyArticles(props) {
  var findArticle = false
  for(let i=0;i<wishListCopy.length;i++){
    if(wishListCopy[i].title == action.articleLiked.title){
        findArticle = true
    }
}

if(!findArticle){
    wishListCopy.push(action.articleLiked)
}

  return (
    <div>
         
            <Nav/>

            <div className="Banner"/>
          
            {props.wishList.map((article,i) => (
            <div className="Card">
          
                    <div  style={{display:'flex',justifyContent:'center'}}>
                      <Card
                        style={{  
                          width: 300, 
                          margin:'15px', 
                          display:'flex',
                          flexDirection: 'column',
                          justifyContent:'space-between' }}
                        cover={
                        <img
                            alt="example"
                            src={article.image} 
                        />
                        }
                        actions={[
                          <Icon type="read" key="ellipsis2" />,
                          <Icon type="delete" key="ellipsis" onClick={() =>{props.deleteToWishList(article.title)}}/>
                        ]}
                        >
                        <Meta
                          title={article.title}
                          description={article.description}
                        />
                      </Card>
                    </div>
                    
             </div>
           ))}
      </div>
  );
}

function mapStateToProps(state) {
  console.log(state)
  return {wishList : state.wishList }
  }
  function mapDispatchToProps(dispatch) {
    return {
      deleteToWishList(title) {
        dispatch({ type: "deleteArticle", title });
      },
    };
  }
  export default connect(
  mapStateToProps, mapDispatchToProps
  )(ScreenMyArticles);
